mapper.select:
select p.*,u.nick_name AS creatorNickName from page p left join user u on p.creator_uid = u.id


        and p.id = #{id}


        and p.name = #{name}


        and p.project_id = #{projectId}


        and p.category_id = #{categoryId}


        and p.description = #{description}


        and p.img_url = #{imgUrl}


        and p.img_height = #{record}


        and p.img_width = #{record}


        and p.device_id = #{record}


        and p.creator_uid = #{creatorUid}


        and p.create_time = #{createTime}


        and p.window_hierarchy_json = #{windowHierarchyJson}


order by p.create_time desc
