mapper.select:
select * from device


        and id = #{id}


        and name = #{name}


        and agent_ip = #{agentIp}


        and agent_port = #{agentPort}


        and system_version = #{systemVersion}


        and cpu_info = #{cpuInfo}


        and mem_size = #{memSize}


        and resolution = #{resolution}


        and img_url = #{imgUrl}


        and type = #{type}


        and status = #{status}


        and stf_status = #{stfStatus}


        and macaca_status = #{macacaStatus}


        and last_online_time = #{lastOnlineTime}


        and last_offline_time = #{lastOfflineTime}


        and username = #{username}


        and create_time = #{createTime}


order by status,create_time desc
