mapper.select:
select p.*,u.nick_name AS creatorNickName from test_plan p left join user u on p.creator_uid = u.id


        and p.id = #{id}


        and p.name = #{name}


        and p.description = #{description}


        and p.project_id = #{projectId}


        and p.creator_uid = #{creatorUid}


        and p.create_time = #{createTime}


order by p.create_time desc
