mapper.select:
select t.*,u.nick_name AS creatorNickName,p.name AS testPlanName from test_task t
left join user u on t.creator_uid = u.id
left join test_plan p on t.test_plan_id = p.id


        and t.id = #{id}


        and t.project_id = #{projectId}


        and t.test_plan_id = #{testPlanId}


        and t.name = #{name}


        and t.description = #{description}


        and t.run_mode = #{runMode}


        and t.status = #{status}


        and t.creator_uid = #{creatorUid}


order by t.commit_time desc
